import logo from './logo.svg';
import "@radix-ui/themes/styles.css";
import { Theme } from "@radix-ui/themes";
import React, { useState, useEffect } from "react";
import { Link as LinkScroll } from "react-scroll";


function App() {
  const [activeLink, setActiveLink] = useState(null);
  const [scrollActive, setScrollActive] = useState(false);
  useEffect(() => {
    window.addEventListener("scroll", () => {
      setScrollActive(window.scrollY > 20);
    });
  }, []);

  return (
    <>

    <header
        className={
          "fixed top-0 w-full  z-30 bg-white-500 transition-all " +
          (scrollActive ? " shadow-md pt-0" : " pt-4")
        }
      >
        <nav className="max-w-screen-xl px-6 sm:px-8 lg:px-16 mx-auto grid grid-flow-col py-3 sm:py-4">
        <ul className="hidden lg:flex col-start-4 col-end-8 text-black-500  items-center">
            <LinkScroll
              activeClass="active"
              to="about"
              spy={true}
              smooth={true}
              duration={1000}
              onSetActive={() => {
                setActiveLink("about");
              }}
              className={
                "px-4 py-2 mx-2 cursor-pointer animation-hover inline-block relative" +
                (activeLink === "about"
                  ? " text-orange-500 animation-active "
                  : " text-black-500 hover:text-orange-500 a")
              }
            >
              About
            </LinkScroll>
            <LinkScroll
              activeClass="active"
              to="feature"
              spy={true}
              smooth={true}
              duration={1000}
              onSetActive={() => {
                setActiveLink("feature");
              }}
              className={
                "px-4 py-2 mx-2 cursor-pointer animation-hover inline-block relative" +
                (activeLink === "feature"
                  ? " text-orange-500 animation-active "
                  : " text-black-500 hover:text-orange-500 ")
              }
            >
              Feature
            </LinkScroll>
            </ul>
        </nav>

      </header>

   <body className="bg-gray-800 h-screen">
     <div className="grid grid-flow-row justify-center h-full">
      <div id="about" className="flex items-center gap-4">
        <div>
          <h1 className="text-white text-3xl lg:text-4xl">Larissa Martins</h1>
          <p className="text-white">Software Engineer</p><br/>
          <p className="text-white">ðŸŽ“ Software Engineering - Bachelor's degree - PUCRS</p>
          <p className="text-white">ðŸ’» Software Quality Engineer at HP Inc.</p>
        </div>
        <img className="rounded-full w-64 h-64" alt="Larissa Fiorini" src="https://avatars.githubusercontent.com/u/7658213?v=4"/>

      </div>

      <div id="skills" className="flex items-center gap-4">
        <div>
          <h1 className="text-white text-3xl lg:text-4xl">Skills</h1>
          <br/>
        </div>

      </div>

      <div id="projects" className="flex items-center gap-4">
        <div>
          <h1 className="text-white text-3xl lg:text-4xl">Projects</h1>
          <br/>
          <div className="border-2 border-gray-500 hover:border-orange-500 transition-all rounded-lg p-8 flex flex-col">
            <p className="text-lg text-white bold capitalize">in.Orbit</p>
          </div>
        </div>

      </div>

    </div>
   </body>
   </>
  );
}

export default App;
